import { Icon } from "leaflet";
import React, { useState, useEffect } from "react";
import mapa from "../mapa.css";
import "leaflet/dist/leaflet.css";
import "../services/MapaService";
import jwtDecode from "jwt-decode";

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

const Mapa = () => {
  const [porudzbine, setPorudzbine] = useState([]);
  const [porudzbineDisplay, setPorudzbineDisplay] = useState([]);
  const [final, setFinal] = useState([]);
  const markers = [];
  const customIcon = new Icon({
    iconUrl: require("../images/marker-icon.png"),
    iconSize: [38, 38],
  });

  function getLatLongForAddress(address) { //Danila Kiša 36, Novi Sad, Srbija
    const nominatimUrl = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
      address
    )}`;

    return fetch(nominatimUrl)
      .then((response) => response.json())
      .then((data) => {
        if (data && data.length > 0) {
          //console.log(data);
          const firstResult = data[0];
          const latitude = parseFloat(firstResult.lat);
          const longitude = parseFloat(firstResult.lon);

          console.log("Latitude: "+latitude +", Longitude: "+longitude);

          return { latitude, longitude };
        } else {
          console.error("Adresa nije pronađena.");
          return null;
        }
      })
      .catch((error) => {
        console.error("Greška prilikom dohvatanja podataka:", error);
        return null;
      });
  }

  useEffect(() => {
    var token = localStorage.getItem("token");
    const decodedToken = jwtDecode(token);

    fetch(
      `https://localhost:44388/Porudzbina/allPorudzbineProdavcaNove?korisnikId=${decodedToken["Id"]}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        mode: "cors",
      }
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setPorudzbine(data);
      })
      .catch((error) => {
        console.error(
          "Greška prilikom dobavljanja podataka sa servera:",
          error
        );
      });

    let filtriranePorudzbine = porudzbine.map(function(porudzbina) {
      return {
        id: porudzbina.id,
        adresaDostave: porudzbina.adresaDostave,
      };
    });
    let porudzbineDisplay = [];
    console.log(filtriranePorudzbine);

    for (const porudzbina of filtriranePorudzbine) {
      const { id, adresaDostave } = porudzbina;
      const latLong = getLatLongForAddress(adresaDostave + ", Novi Sad, Srbija");
  
      if (latLong) {
        porudzbineDisplay.push({
          id: id,
          latitude: latLong.latitude,
          longitude: latLong.longitude,
        });
      }
    }
    console.log("Porudžbine sa dodatnim informacijama:"+ porudzbineDisplay);
    setPorudzbineDisplay(porudzbineDisplay);

    let temp = porudzbineDisplay.map(function (porudzbina) {
      return {
        id: porudzbina.id,
        geocode: [porudzbina.latitude, porudzbina.longitude],
      };
    });

    

    console.log("FINALLLLLLLLLLLLLLLLLLLLLLL: " + temp);

  }, []);

  return (
    <MapContainer center={[45.267136, 19.833549]} zoom={13}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {final.map((marker) => (
        <Marker key={marker.id} position={marker.geocode} icon={customIcon}>
          <Popup>
            <p></p>
            <button>Posalji porudzbinu</button>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default Mapa;
