import { Icon } from "leaflet";
import React, { useState, useEffect } from "react";
import mapa from "../mapa.css";
import "leaflet/dist/leaflet.css";
import "../services/MapaService";
import jwtDecode from "jwt-decode";

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

const Mapa = () => {
  const [porudzbine, setPorudzbine] = useState([]);
  const [porudzbineDisplay, setPorudzbineDisplay] = useState([]);
  const [final, setFinal] = useState([]);
  const markers = [];

  const customIcon = new Icon({
    iconUrl: require("../images/marker-icon.png"),
    iconSize: [38, 38],
  });

  function dobaviGeoKoordinate(porudzbine){
    var filtriranePorudzbine = porudzbine.map(function(porudzbina) {
      return {
        id: porudzbina.id,
        adresaDostave: porudzbina.adresaDostave,
      };
    });

    console.log("JEsmo li stigli do ovde?");

  }


  

  

  useEffect(() => {
    var token = localStorage.getItem("token");
    const decodedToken = jwtDecode(token);

    fetch(
      `https://localhost:44388/Porudzbina/allPorudzbineProdavcaNove?korisnikId=${decodedToken["Id"]}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        mode: "cors",
      }
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setPorudzbine(data);
        dobaviGeoKoordinate(porudzbine);
      })
      .catch((error) => {
        console.error(
          "Greška prilikom dobavljanja podataka sa servera:",
          error
        );
      });
  }, []);

  

  return (
    <MapContainer center={[45.267136, 19.833549]} zoom={13}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {final.map((marker) => (
        <Marker key={marker.id} position={marker.geocode} icon={customIcon}>
          <Popup>
            <p></p>
            <button>
              Posalji porudzbinu
            </button>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default Mapa;
