import { Icon } from "leaflet";
import React, { useState, useEffect } from "react";
import mapa from "../mapa.css";
import "leaflet/dist/leaflet.css";
import "../services/MapaService";

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";





const Mapa = () => {

  const [porudzbina, setPorudzbine] = useState([]);
    const markers = [
        {
          geocode: [45.238911, 19.842487]
        },
        {
          geocode: [45.235800, 19.847482]
        },
        {
          geocode: [45.253375, 19.860579]
        }
      ];
    const customIcon=new Icon({
      iconUrl: require("../images/marker-icon.png"),
      iconSize: [38,38]
    });
    function dobaviGeoKoordinate(){
      let koordinate;
      const address="Aleksa Santic 8";
      const nominatimUrl=`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(address)}`;
      
      fetch(nominatimUrl)
        .then((response)=>response.json())
        .then((data)=>{
          if (data && data.length > 0) {
            const firstResult = data[0];

             koordinate=[
              {
                latitude: parseFloat(firstResult.lat),
                longitude: parseFloat(firstResult.lon)
              }
            ];

            console.log(`Latitude: ${parseFloat(firstResult.lat)}, Longitude: ${parseFloat(firstResult.lon)}`);
          } else {
            console.error("Adresa nije pronađena.");
          }
        })
        .catch((error)=>{
          console.error("Greška prilikom dohvatanja podataka:", error);
        });
        
        return koordinate;
    }






    
  return (
    <MapContainer center={[45.267136, 19.833549]} zoom={13}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {
        markers.map(marker=>(
            <Marker position={marker.geocode} icon={customIcon}>
            <Popup>
            <p></p>
            <button>Posalji porudzbinu</button>
            </Popup>
          </Marker>
        ))
      }

    </MapContainer>
  );
};

export default Mapa;
