import { Icon } from "leaflet";
import React, { useState, useEffect } from "react";
import mapa from "../mapa.css";
import "leaflet/dist/leaflet.css";
import "../services/MapaService";
import jwtDecode from "jwt-decode";

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

const Mapa = () => {
  const [porudzbine, setPorudzbine] = useState([]);
  const markers = [];
  const customIcon = new Icon({
    iconUrl: require("../images/marker-icon.png"),
    iconSize: [38, 38],
  });

  // Funkcija koja će dohvatiti latitude i longitude za datu adresu
  function getLatLongForAddress(address) {
    const nominatimUrl = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
      address
    )}`;

    return fetch(nominatimUrl)
      .then((response) => response.json())
      .then((data) => {
        if (data && data.length > 0) {
          const firstResult = data[0];
          const latitude = parseFloat(firstResult.lat);
          const longitude = parseFloat(firstResult.lon);

          return { latitude, longitude };
        } else {
          console.error("Adresa nije pronađena.");
          return null;
        }
      })
      .catch((error) => {
        console.error("Greška prilikom dohvatanja podataka:", error);
        return null;
      });
  }

  function dobaviGeoKoordinate(porudzbine) {
    var filtriranePorudzbine = porudzbine.map(function(porudzbina) {
      return {
        id: porudzbina.id,
        adresaDostave: porudzbina.adresaDostave,
      };
    });

    const porudzbineDisplay = [];

    // Iterirajte kroz filtrirane porudžbine i dobijte latitude i longitude za svaku adresu
    Promise.all(
      filtriranePorudzbine.map(async (porudzbina) => {
        const { id, adresaDostave } = porudzbina;
        const latLong = await getLatLongForAddress(adresaDostave);

        if (latLong) {
          porudzbineDisplay.push({
            id: id,
            latitude: latLong.latitude,
            longitude: latLong.longitude,
          });
        }
      })
    ).then(() => {
      console.log("Porudžbine sa dodatnim informacijama:", porudzbineDisplay);
    });
  }

  useEffect(() => {
    var token = localStorage.getItem("token");
    const decodedToken = jwtDecode(token);
    //console.log(decodedToken["Id"]);
    if (!token) {
      console.error("Token nije prisutan u localStorage-u.");
      return;
    }
    // Pretpostavićemo da se JWT token sastoji iz tri dela (header, payload, signature) razdvojenih tačkom.
    var tokenParts = token.split(".");
    if (tokenParts.length !== 3) {
      console.error("Neispravan format tokena.");
      return;
    }

    fetch(
      `https://localhost:44388/Porudzbina/allPorudzbineProdavcaNove?korisnikId=${decodedToken["Id"]}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        mode: "cors",
      }
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(JSON.stringify(data));
        setPorudzbine(data);
      })
      .catch((error) => {
        console.error(
          "Greška prilikom dobavljanja podataka sa servera:",
          error
        );
      });
  }, []);

  return (
    <MapContainer center={[45.267136, 19.833549]} zoom={13}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {markers.map((marker) => (
        <Marker position={marker.geocode} icon={customIcon}>
          <Popup>
            <p></p>
            <button>Posalji porudzbinu</button>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default Mapa;
