import { Icon } from "leaflet";
import mapa from "../mapa.css";
import "leaflet/dist/leaflet.css";

import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";



const Mapa = () => {

    const markers = [
        {
          geocode: [45.238911, 19.842487],
          popUp: "Hello, I am pop up 1"
        },
        {
          geocode: [45.235800, 19.847482],
          popUp: "Hello, I am pop up 2"
        },
        {
          geocode: [45.253375, 19.860579],
          popUp: "Hello, I am pop up 3"
        }
      ];
    const customIcon=new Icon({
      iconUrl: require("../images/marker-icon.png"),
      iconSize: [38,38]
    });

    function dobaviGeoKoordinate(){
      const address="Aleksa Santic 8";
      const nominatimUrl=`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(address)}`;
      
      fetch(nominatimUrl)
        .then((response)=>response.json())
        .then((data)=>{
          if (data && data.length > 0) {
            const firstResult = data[0];
            const latitude = parseFloat(firstResult.lat);
            const longitude = parseFloat(firstResult.lon);
      
            console.log(`Latitude: ${latitude}, Longitude: ${longitude}`);
          } else {
            console.error("Adresa nije pronađena.");
          }
        })
        .catch((error)=>{
          console.error("Greška prilikom dohvatanja podataka:", error);

        })

    }
    
  return (
    <MapContainer center={[45.267136, 19.833549]} zoom={13}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {
        markers.map(marker=>(
            <Marker position={marker.geocode} icon={customIcon}>
            <Popup>
            <p></p>
            <button>Posalji porudzbinu</button>
            </Popup>
          </Marker>
        ))
      }

    </MapContainer>
  );
};

export default Mapa;
